import './App.css';

function ThingsToDo(props) {

    const clearItem = () => {
        props.setToDoArray(props.toDoArray.filter((toDoItem) => (toDoItem!==e.target.value)))
    }

    const theToDoList = () => {
        return(
                props.toDoArray.map((item) => (<div className="toDoListItem"><input type="checkbox"></input>{item}<button type="submit" onClick= {e => clearItem(e.target.value)} value={item}>Clear</button></div>))    
            )        
    }



    return (
        <div>
        <p>a list of things to do will go here, with a button to delete when finished (or complete)?</p>
        <ul>{theToDoList()}</ul>
        </div>
    )
}







export default ThingsToDo;
