import './App.css';

function ThingsToDo(props) {

    const clearItem = (item) => {
        props.setToDoArray(props.toDoArray.filter((toDoItem) => (toDoItem!==item)))
    }

    const theToDoList = () => {
        return(
                props.toDoArray.map((item) => (<div><div className="toDoListItem"><input type="checkbox"></input>{item}</div><div className="clear-button-div"></div><button type="submit" onClick= {e => clearItem(item)} >Clear</button></div>))    
            )        
    }



    return (
        <div>
        {props.toDoArray.length > 0 ? <ul>{theToDoList()}</ul> : <p className="nothing">Nothing to do!</p>}
        </div>
    )
}







export default ThingsToDo;
